
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int friends = 10;
		
		friends = friends + 1;
		System.out.println(friends);
		friends = friends - 1;
		System.out.println(friends);
		friends = friends * 2;
		System.out.println(friends);
		friends = friends / 1;
		System.out.println(friends);
		friends = friends % 3;
		System.out.println(friends);
		friends = friends % 2;		
		System.out.println(friends);
		
		friends = 10;
		
		double friend = (double)friends / 3;
		System.out.println(friend);
		
		
	}

}
